sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();

	// Query the database for NeedIt Task records with Due date field values between current
	// time and 24 hours from now.  Only return NeedIt Task records that do not have
	// a State field value of Closed Complete (3).
	var almostDueNITasks = new GlideRecord('x_58872_needit_needit_task');
	almostDueNITasks.addQuery('due_date','>=',rightNow);
	almostDueNITasks.addQuery('due date','<',gs.hoursAgo(-24));
	almostDueNITasks.addQuery('state','!=',3);

	overdueNITasks.query();
	// Write a log message for each NeedIt Task Record due within 24 hours
	while(almostDueNITasks.next()){
		gs.info("Almost due NeedIt Task record = " + overdueNITasks.number);
		gs.eventQueue('x_58872_needit.almostDueNeedItTasks',almostDueNITasks,almostDueNITasks.number,gs.getUserName());
	}
